glsl3.vert:
precision highp float;
uniform CCGlobal {
  mat4 cc_matView;
  mat4 cc_matViewInv;
  mat4 cc_matProj;
  mat4 cc_matProjInv;
  mat4 cc_matViewProj;
  mat4 cc_matViewProjInv;
  vec4 cc_cameraPos;
  vec4 cc_time;
  mediump vec4 cc_screenSize;
  mediump vec4 cc_screenScale;
};
uniform CCLocal {
  mat4 cc_matWorld;
  mat4 cc_matWorldIT;
};
in vec3 a_position;
in vec4 a_color;
out vec4 v_color;
#if USE_TEXTURE
in vec2 a_uv0;
out vec2 v_uv0;
#endif
void main () {
  vec4 pos = vec4(a_position, 1);
  #if CC_USE_MODEL
  pos = cc_matViewProj * cc_matWorld * pos;
  #else
  pos = cc_matViewProj * pos;
  #endif
  #if USE_TEXTURE
  v_uv0 = a_uv0;
  #endif
  v_color = a_color;
  gl_Position = pos;
}

glsl3.frag:
precision highp float;
#if USE_ALPHA_TEST
  uniform ALPHA_TEST {
    float alphaThreshold;
  };
#endif
void ALPHA_TEST (in vec4 color) {
  #if USE_ALPHA_TEST
      if (color.a < alphaThreshold) discard;
  #endif
}
void ALPHA_TEST (in float alpha) {
  #if USE_ALPHA_TEST
      if (alpha < alphaThreshold) discard;
  #endif
}
in vec4 v_color;
#if USE_TEXTURE
in vec2 v_uv0;
uniform sampler2D texture;
#endif
#if ENABLE_LIGHT
uniform Light {
  vec4 lightColor;
  vec2 lightCenterPoint;
  float lightAngle;
  float lightWidth;
  float enableGradient;
  float cropAlpha;
  float enableFog;
};
vec4 addLightColor(vec4 textureColor, vec4 lightColor, vec2 lightCenterPoint, float lightAngle, float lightWidth) {
  if (lightWidth <= 0.0) {
    return textureColor;
  }
  float angleInRadians = radians(lightAngle);
  float dis = 0.0;
  if (mod(lightAngle, 180.0) != 0.0) {
    float lightOffsetX = lightCenterPoint.x - ((1.0 - lightCenterPoint.y) / tan(angleInRadians));
    float dx = lightOffsetX + (1.0 - v_uv0.y) / tan(angleInRadians);
    float offsetDis = abs(v_uv0.x - dx);
    dis = sin(angleInRadians) * offsetDis;
  } else {
    dis = abs(v_uv0.y - lightCenterPoint.y);
  }
  float a = 1.0 ;
  if (bool(cropAlpha)) {
    a *= step(0.01, textureColor.a);
  }
  if (!bool(enableFog)) {
    a *= step(dis, lightWidth * 0.5);
  }
  if (bool(enableGradient)) {
    a *= 1.0 - dis / (lightWidth * 0.5);
  }
  vec4 finalLightColor = lightColor * a;
  return textureColor * textureColor.a + finalLightColor;
}
#endif
void main () {
  vec4 o = vec4(1, 1, 1, 1);
  #if USE_TEXTURE
  o *= texture(texture, v_uv0);
    #if CC_USE_ALPHA_ATLAS_TEXTURE
    o.a *= texture2D(texture, v_uv0 + vec2(0, 0.5)).r;
    #endif
  #endif
  o *= v_color;
  ALPHA_TEST(o);
  gl_FragColor = o;
  #if ENABLE_LIGHT
  gl_FragColor = addLightColor(gl_FragColor, lightColor, lightCenterPoint, lightAngle, lightWidth);
  #endif
}

